function policyBuilderGeneral(i) {
    "platforms" == i &&
        ($("#input_edit_wizard_platforms_website").prop("checked") ? $("#control_edit_wizard_website_url").show() : $("#control_edit_wizard_website_url").hide(),
        $("#input_edit_wizard_platforms_mobile").prop("checked") ? $("#control_edit_wizard_mobile_name").show() : $("#control_edit_wizard_mobile_name").hide()),
        "premium" == i &&
            ($("#input_edit_wizard_premium_1").prop("checked")
                ? ($("#control_box_wizard_features_premium").show(), $("#control_box_wizard_features_basic").hide())
                : ($("#input_edit_wizard_premium_0").prop("checked") && $("#control_box_wizard_features_basic").show(), $("#control_box_wizard_features_premium").hide()));
}
function policyBuilderOperator(i) {
    "company" == i && ($("#input_edit_wizard_company_1").prop("checked") ? $("#control_edit_wizard_company_name").show() : $("#control_edit_wizard_company_name").hide());
}
function policyBuilderUsers(i) {
    "register" == i &&
        ($("#input_edit_wizard_register_1").prop("checked") ? ($("#control_edit_wizard_share").show(), $("#control_edit_wizard_anon_use").show()) : ($("#control_edit_wizard_share").hide(), $("#control_edit_wizard_anon_use").hide()));
}
function policyBuilderPostings(i) {
    "publish" == i &&
        ($("#input_edit_wizard_publish_1").prop("checked")
            ? ($("#control_edit_wizard_objectionable").show(), $("#control_edit_wizard_adult").show())
            : ($("#control_edit_wizard_objectionable").hide(), $("#control_edit_wizard_adult").hide())),
        ("publish" != i && "objectionable" != i) ||
            ($("#input_edit_wizard_publish_1").prop("checked") && $("#input_edit_wizard_objectionable_1").prop("checked") ? $("#control_edit_wizard_remove").show() : $("#control_edit_wizard_remove").hide());
}
function policyBuilderProducts(i) {
    "sell" == i &&
        ($("#input_edit_wizard_sell_1").prop("checked")
            ? ($("#control_edit_wizard_payments_method").show(), $("#control_edit_wizard_diff_name").show())
            : ($("#control_edit_wizard_payments_method").hide(), $("#control_edit_wizard_diff_name").hide()));
}
function policyBuilderFiles(i) {
    "upload" == i && ($("#input_edit_wizard_upload_1").prop("checked") ? $("#control_edit_wizard_execute").show() : $("#control_edit_wizard_execute").hide());
}
function policyBuilderEmails(i) {
    "send" == i && ($("#input_edit_wizard_send_1").prop("checked") ? $("#control_edit_wizard_bulk").show() : $("#control_edit_wizard_bulk").hide()),
        ("send" != i && "bulk" != i) ||
            ($("#input_edit_wizard_bulk_1").prop("checked") ? ($("#control_edit_wizard_purchased").show(), $("#control_edit_wizard_suspend").show()) : ($("#control_edit_wizard_purchased").hide(), $("#control_edit_wizard_suspend").hide()));
}
function policyBuilderCopyrights(i) {
    "action" == i && ($("#input_edit_wizard_action_1").prop("checked") ? ($("#control_edit_wizard_court").show(), $("#control_edit_wizard_notify").show()) : ($("#control_edit_wizard_court").hide(), $("#control_edit_wizard_notify").hide()));
}
function policyBuilderEnforce(i) {
    "suspend" == i &&
        ($("#input_edit_wizard_suspend_1").prop("checked") ? ($("#control_edit_wizard_action").show(), $("#control_edit_wizard_backup").show()) : ($("#control_edit_wizard_action").hide(), $("#control_edit_wizard_backup").hide())),
        ("suspend" != i && "backup" != i) ||
            ($("#input_edit_wizard_suspend_1").prop("checked") && $("#input_edit_wizard_backup_1").prop("checked") ? $("#control_edit_wizard_backup_fee").show() : $("#control_edit_wizard_backup_fee").hide());
}
function policyBuilderContacts(i) {
    "contacts" == i &&
        ($("#input_edit_wizard_contacts_form").prop("checked") ? $("#control_edit_wizard_contact_form").show() : $("#control_edit_wizard_contact_form").hide(),
        $("#input_edit_wizard_contacts_email").prop("checked") ? $("#control_edit_wizard_contact_email").show() : $("#control_edit_wizard_contact_email").hide(),
        $("#input_edit_wizard_contacts_address").prop("checked") ? $("#control_edit_wizard_contact_address").show() : $("#control_edit_wizard_contact_address").hide());
}
$(function () {
    "general" == policyWizardStepKey
        ? ($("#control_edit_wizard_platforms input.checkbox").click(function () {
              policyBuilderGeneral("platforms");
          }),
          $("#control_edit_wizard_platforms input.radio").click(function () {
              policyBuilderGeneral("platforms");
          }),
          policyPremiumPaid ||
              $("#control_edit_wizard_premium input.radio").click(function () {
                  policyBuilderGeneral("premium");
              }))
        : "operator" == policyWizardStepKey
        ? $("#control_edit_wizard_company input.radio").click(function () {
              policyBuilderOperator("company");
          })
        : "users" == policyWizardStepKey
        ? $("#control_edit_wizard_register input.radio").click(function () {
              policyBuilderUsers("register");
          })
        : "postings" == policyWizardStepKey
        ? ($("#control_edit_wizard_publish input.radio").click(function () {
              policyBuilderPostings("publish");
          }),
          $("#control_edit_wizard_objectionable input.radio").click(function () {
              policyBuilderPostings("objectionable");
          }))
        : "products" == policyWizardStepKey
        ? $("#control_edit_wizard_sell input.radio").click(function () {
              policyBuilderProducts("sell");
          })
        : "files" == policyWizardStepKey
        ? $("#control_edit_wizard_upload input.radio").click(function () {
              policyBuilderFiles("upload");
          })
        : "emails" == policyWizardStepKey
        ? ($("#control_edit_wizard_send input.radio").click(function () {
              policyBuilderEmails("send");
          }),
          $("#control_edit_wizard_bulk input.radio").click(function () {
              policyBuilderEmails("bulk");
          }))
        : "copyrights" == policyWizardStepKey
        ? $("#control_edit_wizard_action input.radio").click(function () {
              policyBuilderCopyrights("action");
          })
        : "enforce" == policyWizardStepKey
        ? ($("#control_edit_wizard_suspend input.radio").click(function () {
              policyBuilderEnforce("suspend");
          }),
          $("#control_edit_wizard_backup input.radio").click(function () {
              policyBuilderEnforce("backup");
          }))
        : "contacts" == policyWizardStepKey &&
          $("#control_edit_wizard_contacts input.checkbox").click(function () {
              policyBuilderContacts("contacts");
          });
});
