function policyBuilderGeneral(i) {
    "platforms" == i &&
        ($("#input_edit_wizard_platforms_mobile").prop("checked") || $("#input_edit_wizard_platforms_mobile_website").prop("checked") ? $("#control_edit_wizard_mobile_name").show() : $("#control_edit_wizard_mobile_name").hide(),
        $("#input_edit_wizard_platforms_website").prop("checked") || $("#input_edit_wizard_platforms_mobile_website").prop("checked") ? $("#control_edit_wizard_website_url").show() : $("#control_edit_wizard_website_url").hide(),
        $("#input_edit_wizard_platforms_other").prop("checked") ? $("#control_edit_wizard_platforms_other").show() : $("#control_edit_wizard_platforms_other").hide()),
        ("platforms" != i && "platforms_other" != i) ||
            ($("#input_edit_wizard_platforms_other").prop("checked") && $("#input_edit_wizard_platforms_other_course").prop("checked") ? $("#control_edit_wizard_course_name").show() : $("#control_edit_wizard_course_name").hide(),
            $("#input_edit_wizard_platforms_other").prop("checked") && $("#input_edit_wizard_platforms_other_landing").prop("checked") ? $("#control_edit_wizard_landing_url").show() : $("#control_edit_wizard_landing_url").hide(),
            $("#input_edit_wizard_platforms_other").prop("checked") && $("#input_edit_wizard_platforms_other_podcast").prop("checked") ? $("#control_edit_wizard_podcast_name").show() : $("#control_edit_wizard_podcast_name").hide(),
            $("#input_edit_wizard_platforms_other").prop("checked") && $("#input_edit_wizard_platforms_other_ebook").prop("checked") ? $("#control_edit_wizard_ebook_name").show() : $("#control_edit_wizard_ebook_name").hide(),
            $("#input_edit_wizard_platforms_other").prop("checked") && $("#input_edit_wizard_platforms_other_video").prop("checked") ? $("#control_edit_wizard_video_name").show() : $("#control_edit_wizard_video_name").hide()),
        "premium" == i &&
            ($("#input_edit_wizard_premium_1").prop("checked")
                ? ($("#control_box_wizard_features_premium").show(), $("#control_box_wizard_features_basic").hide())
                : ($("#input_edit_wizard_premium_0").prop("checked") && $("#control_box_wizard_features_basic").show(), $("#control_box_wizard_features_premium").hide()));
}
function policyBuilderOperator(i) {
    "company" == i &&
        ($("#input_edit_wizard_company_1").prop("checked")
            ? ($("#control_edit_wizard_company_name").show(), $("#control_edit_wizard_person_name").hide())
            : ($("#control_edit_wizard_company_name").hide(), $("#control_edit_wizard_person_name").show()));
}
function policyBuilderPostings(i) {
    "publish" == i &&
        ($("#input_edit_wizard_publish_1").prop("checked")
            ? ($("#control_edit_wizard_monitor").show(), $("#control_edit_wizard_reuse").show(), $("#control_edit_wizard_terminate").show())
            : ($("#control_edit_wizard_monitor").hide(), $("#control_edit_wizard_reuse").hide(), $("#control_edit_wizard_terminate").hide()));
}
function policyBuilderPraise(i) {
    "testimonials" == i &&
        ($("#input_edit_wizard_testimonials_1").prop("checked")
            ? ($("#control_edit_wizard_modify").show(), $("#control_edit_wizard_compensate").show())
            : ($("#control_edit_wizard_modify").hide(), $("#control_edit_wizard_compensate").hide()));
}
function policyBuilderSponsorship(i) {
    "accept" == i &&
        ($("#input_edit_wizard_accept_1").prop("checked") ? ($("#control_edit_wizard_identify").show(), $("#control_edit_wizard_influence").show()) : ($("#control_edit_wizard_identify").hide(), $("#control_edit_wizard_influence").hide()));
}
function policyBuilderMarketing(i) {
    "affiliate_links" == i && ($("#input_edit_wizard_affiliate_links_1").prop("checked") ? $("#control_edit_wizard_affiliate_links_amazon").show() : $("#control_edit_wizard_affiliate_links_amazon").hide());
}
function policyBuilderContacts(i) {
    "contacts" == i &&
        ($("#input_edit_wizard_contacts_form").prop("checked") ? $("#control_edit_wizard_contact_form").show() : $("#control_edit_wizard_contact_form").hide(),
        $("#input_edit_wizard_contacts_email").prop("checked") ? $("#control_edit_wizard_contact_email").show() : $("#control_edit_wizard_contact_email").hide(),
        $("#input_edit_wizard_contacts_address").prop("checked") ? $("#control_edit_wizard_contact_address").show() : $("#control_edit_wizard_contact_address").hide());
}
$(function () {
    "general" == policyWizardStepKey
        ? ($("#control_edit_wizard_platforms input.checkbox").click(function () {
              policyBuilderGeneral("platforms");
          }),
          $("#control_edit_wizard_platforms input.radio").click(function () {
              policyBuilderGeneral("platforms");
          }),
          $("#control_edit_wizard_platforms_other input.radio").click(function () {
              policyBuilderGeneral("platforms_other");
          }),
          policyPremiumPaid ||
              $("#control_edit_wizard_premium input.radio").click(function () {
                  policyBuilderGeneral("premium");
              }))
        : "operator" == policyWizardStepKey
        ? $("#control_edit_wizard_company input.radio").click(function () {
              policyBuilderOperator("company");
          })
        : "postings" == policyWizardStepKey
        ? $("#control_edit_wizard_publish input.radio").click(function () {
              policyBuilderPostings("publish");
          })
        : "praise" == policyWizardStepKey
        ? $("#control_edit_wizard_testimonials input.radio").click(function () {
              policyBuilderPraise("testimonials");
          })
        : "sponsorship" == policyWizardStepKey
        ? $("#control_edit_wizard_accept input.radio").click(function () {
              policyBuilderSponsorship("accept");
          })
        : "marketing" == policyWizardStepKey
        ? $("#control_edit_wizard_affiliate_links input.radio").click(function () {
              policyBuilderMarketing("affiliate_links");
          })
        : "contacts" == policyWizardStepKey &&
          $("#control_edit_wizard_contacts input.checkbox").click(function () {
              policyBuilderContacts("contacts");
          });
});
