function dmcaGenerateNotice() {
    $("#dmcanotice-form .error").remove();
    var n = {};
    for (var e in { first_name: "", last_name: "", address: "", city: "", country: "", email: "", infringing_urls: "", original_urls: "", description: "" })
        (n[e] = $("#input_edit_dmca_" + e)
            .val()
            .trim()),
            0 == n[e].length && $("#input_edit_dmca_" + e).after('<span class="help error">This field is required.</span>');
    for (var e in { zip: "", state: "", phone: "" })
        n[e] = $("#input_edit_dmca_" + e)
            .val()
            .trim();
    if ($("#dmcanotice-form .error").length) return !1;
    var t =
        "To whom it may concern,\n\nMy name is " +
        n.first_name +
        " " +
        n.last_name +
        " and I am writing to notify you of the copyright infringement and unlawful use of my copyrighted material that appear on the service for which you are the designated agent.\n\nThe infringing material, which I contend belongs to me, includes the following:\n" +
        n.description +
        "\n\nThe infringing material appears at the following location:\n" +
        n.infringing_urls +
        "\n\nThe original material is available at the following location:\n" +
        n.original_urls +
        "\n\nThis letter is the official notification under Section 512(c) of the Digital Millennium Copyright Act of 1998 (”DMCA”) and I request the immediate removal of the aforementioned infringing material from your servers.\n\nI also request that you immediately notify the infringer of this notice and inform them to cease any further posting of the infringing material to your server in the future.\n\nI am providing this notice in good faith and with the reasonable belief that the use of the described material in the manner complained is not authorized by myself, my agents, or the law.\n\nI swear, under penalty of perjury, that the information in the notification is accurate and that I am the owner of the copyrighted material involved.\n\nIf you have any questions, please contact me directly at " +
        n.email +
        "\n\nSincerely,\n\n" +
        n.first_name +
        " " +
        n.last_name +
        "\n\n" +
        n.address +
        "\n" +
        n.city +
        (n.state ? ", " + n.state : "") +
        (n.zip ? ", " + n.zip : "") +
        "\n" +
        n.country +
        "\n" +
        (n.phone ? n.phone + "\n" : n.phone) +
        n.email;
    $("#dmcanotice-form").hide(), $("#dmcanotice-text").html(t), $("#dmcanotice-preview").show();
}
function dmcaModifyNotice() {
    $("#dmcanotice-preview").hide(), $("#dmcanotice-form").show();
}
$(function () {
    var n = new ClipboardJS("#dmca_copy_button");
    $("#dmca_copy_button").focusout(function () {
        $("#dmca_copy_button").html("Copy my DMCA notice");
    }),
        n.on("success", function () {
            $("#dmca_copy_button").html("Copied!");
        }),
        n.on("error", function () {
            $("#dmca_copy_button").html("Press Ctrl+C to copy");
        });
});
